package com.grepp.moodlink.app.model.details;


import com.grepp.moodlink.app.model.details.dto.BookDetailsDto;
import com.grepp.moodlink.app.model.details.dto.MovieDetailsDto;
import com.grepp.moodlink.app.model.details.dto.SongDetailsDto;
import com.grepp.moodlink.app.model.like.LikeService;
import java.time.LocalDateTime;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;

@Service
@Slf4j
@RequiredArgsConstructor
public class DetailsService {


    // db에서 가져오자. 현재는 likeService 내부의 like상태를 가져와 쓰는중
    private final LikeService likeService;

    public BookDetailsDto getBookDetails(String id) {
        // db에서 가져오기
        String img1 ="https://shopping-phinf.pstatic.net/main_5432863/54328632105.20250421201908.jpg";
        String img2 ="data:image/jpeg;base64,";

        boolean liked = likeService.getBookStatus(id);

        if(id.equals("b1111")){
            return new BookDetailsDto("b1111","캐치캐치티니핑핑","장영실", LocalDateTime.now(),"가나다라", img1, liked);
        }
        return new BookDetailsDto("b2222","어린이필독도서","엄복동", LocalDateTime.now(), "마바사",img2, liked);
    }

    public SongDetailsDto getSongDetails(String id) {
        // db에서 가져오기
        String img1 ="data:image/jpeg;base64,";
        String img2 ="data:image/jpeg;base64,";

        boolean liked = likeService.getSongStatus(id);

        if(id.equals("s1111")){
            return new SongDetailsDto("s1111","luckycolor","aimyon", LocalDateTime.now(),"아자차카",img1, liked);
        }
        return new SongDetailsDto("s2222","럭키컬러","아이유 말고 아이묭", LocalDateTime.now(),"타파하",img2, liked);
    }


    public MovieDetailsDto getMovieDetails(String id) {
        String img1 = "data:image/jpeg;base64,";
        String img2 = "data:image/jpeg;base64,";

        boolean liked = likeService.getMovieStatus(id);

        if(id.equals("m1111")){
            return new MovieDetailsDto("m1111","베트맨 3부작","크리스토퍼 놀란", "오만과 편견의 장르","초코민토 요리모 아나타",img1,liked);
        }
        return new MovieDetailsDto("m2222","쇼생크 탈출","놀란 크리스토퍼 놀란", "무서운게 딱 좋아", "퉁퉁퉁퉁 사후르",img2, liked);
    }
}
